int p;

bool isprime(int x)
{
	for(int i=2;i*i<=x;i++)
	{
		if(x%i==0)return false;
	}
	return true;
}

template <typename tp>
void read(tp &x)
{
	char ch=getchar();x=0;bool f=0;
	while(ch>'9'||ch<'0'){if(ch=='-')f=1;ch=getchar();}
	while(ch>='0'&&ch<='9'){x=x*10+ch-48;ch=getchar();}
	if(f)x=-x;
}

int solve(int n)
{
	int ans=0;
	for(long long i=0;i<n;i++)
	{
		long long tmp=(n-i)*(n+i);
		long long tmp2=sqrtl(tmp)+1e-6;
		if(tmp2*tmp2==tmp)ans++;
	}
	return ans*4;
}

int main()
{
	long long n;
	read(n);
	long long n2=1;
	while(n>1&&(puseless<nuseless||pusefull<nusefull))
	{
		while(puseless<nuseless&&n%uselessprime[puseless]==0)
		{
			n/=uselessprime[puseless];
		}
		puseless++;
		bool flag=false;
		while(pusefull<nusefull&&n%usefullprime[pusefull]==0)
		{
			flag=true;
			n/=usefullprime[pusefull];
			n2*=usefullprime[p];
		}
		if(flag)p++;
		pusefull++;
	}
	if(n>1)puts("4");else cout<<solve(n2);
	return 0;
}

